    	    	if((user.getNom().isEmpty())){
    		return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("Le Nom est vide");
    	}
    	    	if((user.getPrenom().isEmpty())){
    	    		return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("Le Prenom est vide");
    	    	}
    	
    	    	
    	    	else if (!ptr.matcher(user.getEmail()).matches()) {
    		return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("L'email que vous avez saisie n'est pas valide");
		}
    	else if (!service.findUserByEmail(user.getEmail()).isEmpty()) {
			return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("L'email : "+ user.getEmail() +" est déjà utilisé");
		}
		
		
		else if (!expressRegPassword.matcher(user.getPassword()).matches()) {
			return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("Le mot de passe doit etre : de 8 à 15 caractères\r\n"
					+ "- au moins une lettre minuscule\r\n"
					+ "- au moins une lettre majuscule\r\n"
					+ "- au moins un chiffre\r\n"
					+ "- au moins un de ces caractères spéciaux: !@#&()–[{}]:;',?/*~$^+=<>\r\n"
					+ "- aucun autre caractère possible: pas de & par exemple");
		}
		else if((user.getPassword().compareTo(user.getConfirmPassword()))!=0){
			return ResponseEntity.status(HttpStatus.BAD_REQUEST).body("Le deux mots de passe ne sont pas les memes");
		}
		else{
    	user.setPassword(passwordEncoder.encode(user.getPassword()));
    	service.saveAndUpdate(user);
    	return ResponseEntity.status(HttpStatus.ACCEPTED).body("Votre compte a bien été crée");}
